import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import {} from '@angular/router';
@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],

})
export class AppComponent {
  title = 'hotel-advisor';

  readonly ROOT_URL = 'https://4001.hoteladvisor.net/'

  posts: any;

  constructor(private http: HttpClient) {}

getPosts(){
  this.posts = this.http.get(this.ROOT_URL+ '/posts')
}

}
<app-header></app-header>
<router-outlet>

</router-outlet>
<div class= "container">
    <div class="homebackground"><img class="logo" src="/assets/img/logoElektra.png" alt="image">


            <form class="example-form">
                <mat-form-field class="example-full-width" appearance="fill">
                  <mat-label>Kart Numarası</mat-label>
                  <input matInput placeholder="6 haneli kart numaranızı giriniz." value="">
                </mat-form-field>

              </form>

        <button (click)="getPosts() " class="login-button" mat-raised-button color="primary">Giriş Yap</button></div>
       <div *ngFor="let post of posts | async">

        {{post | json}}
       </div>
